    public bool ppeIsChecked;

    [Parameter]
    public MaintRequestInitiation maintReq { get; set; }


    [Parameter]
    public bool ReqReadOnly { get; set; }

    [Parameter]
    public EventCallback<bool> ReqReadOnlyChanged { get; set; }

    private HubConnection hubConnection;

    public string name;

    public void seeppe()
    {
        foreach (var item in maintReq.PPEEquipment)
        {
            Console.WriteLine(item.Id + "       " + item.PPEName);
        }
    }

    public void DeleteAuthroization()
    {
        GeneralViewModel.RemoveAuthStartToWorkUser();
        ReqReadOnly = false;
        ReqReadOnlyChanged.InvokeAsync(ReqReadOnly);
        if (IsConnected) SendMessage();
    }

    Task SendMessage() => hubConnection.SendAsync("SendMessage");

    private void IsReadOnlyChanged(ChangeEventArgs arg)
    {
        if (ReqReadOnly == true)
        {
            ReqReadOnly = false;
        }
        else if (ReqReadOnly == false)
        {
            ReqReadOnly = true;
        }

    }

    public string colour = "black";

    protected override async Task OnInitializedAsync()
    {

        GeneralViewModel.maintId = maintReq.Id;
        Task.Run(async ()=> { await GeneralViewModel.GetGeneralRequest(maintReq.Id); }).Wait();
        Task.Run(async ()=> { await GeneralViewModel.LoadAllPPE(); }).Wait();
        Task.Run(async ()=> { await GeneralViewModel.LoadStartToworkAuth(); }).Wait();
        Task.Run(async () => { await GeneralViewModel.LoadCompletedUser(); }).Wait();
        Task.Run(async () => { await GeneralViewModel.LoadAllUsers(); }).Wait();
        //GeneralViewModel.LoadCompletedUser();


        //Load the selected isolations from the PPE
        //This is done below so that the genric SquareComboBox will find the PPE
        GeneralViewModel.PPEItemsSelected =
        GeneralViewModel.ppeList
        .Where(i => i.MaintRequestInitiations.Count == 1).ToList();


        hubConnection = new HubConnectionBuilder()
            .WithUrl(NavigationManager.ToAbsoluteUri("/broadcastHub"))
            .Build();

        //hubConnection.On("ReceiveMessage", () =>
        //{
        //    CallLoadData();
        //    StateHasChanged();
        //});

        await hubConnection.StartAsync();




    }

    private void SignOffWorkCompleted(MouseEventArgs e)
    {
        GeneralViewModel.AddNewAuthrization("Approved", "Completed");

        //Task.Run(async () => { await GeneralViewModel.LoadStartToworkAuth(); }).Wait();
        Task.Run(async ()=> { await GeneralViewModel.GetGeneralRequest(maintReq.Id); }).Wait();

        InvokeAsync(() => StateHasChanged());
    }


    private void StartWorkRequest(MouseEventArgs e)
    {
        GeneralViewModel.AddNewAuthrization("Requested", "AuthorityToWork");
        GeneralViewModel.LoadStartToworkAuth();

        if (ReqReadOnly == true)
        {
            ReqReadOnly = false;
        }
        else if (ReqReadOnly == false)
        {
            ReqReadOnly = true;
        }

        ReqReadOnlyChanged.InvokeAsync(ReqReadOnly);

        Task.Run(async () => { await GeneralViewModel.LoadStartToworkAuth(); }).Wait();

        InvokeAsync(() => StateHasChanged());
    }

    private void CallLoadData()
    {
        Task.Run(async () =>
        {
            await GeneralViewModel.GetGeneralRequest(maintReq.Id);
            GeneralViewModel.LoadStartToworkAuth();
        });
    }

    public bool IsConnected =>
        hubConnection.State == HubConnectionState.Connected;

    public void Dispose()
    {
        _ = hubConnection.DisposeAsync();
    }

