        //check if users have already been assigned and if so remove from the avaiable list
        for(int i = 0; i < NameMaint.Count; i++)
        {
            for (int ii = 0; ii < Assigned.Count; ii++)
            {            
                if (NameMaint[i] == Assigned[ii])
